menuShow();
languageChoose();
function menuShow(){
    const menu = document.querySelector('.nav-list'),
    menuBtn = document.querySelector('.nav-bars');
    menuBtn.addEventListener('click', ()=>{
        if(menu.classList.contains('active')){
            menu.classList.remove('active');
            menuBtn.classList.remove('fa-times');
            menuBtn.classList.add('fa-bars');
        }else{
            menu.classList.add('active');
            menuBtn.classList.add('fa-times');
            menuBtn.classList.remove('fa-bars');
        }
    })
}
function languageChoose(){
    const dropBtnImg = document.querySelector('.dropbtn img'),
    languageBtn = document.querySelectorAll('.dropdown-content__lang');
    languageBtn.forEach(btn=>{
        btn.addEventListener('click', ()=>{
            for(let i=0; i<languageBtn.length; i++){
                languageBtn[i].classList.remove('active')
            }
            if(btn.classList.contains('active')){
                return;
            }else{
                const btnImage = btn.querySelector('img');
                btn.classList.add('active')
                dropBtnImg.src = btnImage.src;
            }

        })
    });
}